pragma solidity >=0.7.4;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/EnumerableSet.sol';

contract CardSpec is Ownable {
    using EnumerableSet for EnumerableSet.UintSet;
    //             identity           |           personality
    // nftSign  skin  rarity  padding |   padding  skill   mintTime  index
    // 16        16     16      40    |     32       32       40       64
    // 240       224    208     168   |     136      104      64       0

    // nft sign id
    uint16 public constant NFT_SIGN = 0x8000;

    uint8 public constant NFT_SIGN_BIT = 240;
    uint8 public constant CARD_SKIN_BIT = 224;
    uint8 public constant CARD_RARITY_BIT = 208;
    uint8 public constant CARD_IDENTITY_BIT = 168;
    uint8 public constant CARD_IDENTITY_PADDING_BIT = 168;
    uint8 public constant CARD_PERSONALITY_PADDING_BIT = 136;
    uint8 public constant CARD_PERSONALITY_SKILL_BIT = 104;
    uint8 public constant CARD_PERSONALITY_MINTTIME_BIT = 64;
    uint8 public constant CARD_PERSONALITY_INDEX_BIT = 0;

    uint256 public constant CARD_IDENTITY_MASK = uint256(~uint88(0)) << CARD_IDENTITY_BIT;
    uint256 public constant CARD_SKIN_MASK = uint256(~uint16(0)) << CARD_SKIN_BIT;
    uint256 public constant CARD_RARITY_MASK = uint256(~uint16(0)) << CARD_RARITY_BIT;
    uint256 public constant CARD_PERSONALITY_SKILL_MASK = uint256(~uint32(0)) << CARD_PERSONALITY_SKILL_BIT;

    uint256 public constant CARD_PERSONALITY_MINTTIME_MASK = uint256(~uint40(0)) << CARD_PERSONALITY_MINTTIME_BIT;
    uint256 public constant CARD_PERSONALITY_INDEX_MASK = uint256(~uint64(0)) << CARD_PERSONALITY_INDEX_BIT;

    struct CardType {
        uint16 skin;
        uint16 rarity;
        bytes32 comment;
    }

    // card type array
    CardType[] public cardTypes;
    EnumerableSet.UintSet private skillTypes;

    constructor() {
        cardTypes.push();
    }

    function addCardType(
        uint16 _skin,
        uint16 _rarity,
        bytes32 _comment
    ) external onlyOwner returns (uint256 cardId) {
        cardId = cardTypes.length;
        CardType storage cardType = cardTypes.push();
        cardType.skin = _skin;
        cardType.rarity = _rarity;
        cardType.comment = _comment;
    }

    function addSkillType(uint32 _skill) external onlyOwner returns (bool) {
        return skillTypes.add(_skill);
    }

    function getCardTypesLength() external view returns (uint256) {
        return cardTypes.length;
    }

    function getTokenSkin(uint256 tokenId) public pure returns (uint16) {
        return uint16(tokenId >> CARD_SKIN_BIT);
    }

    function getTokenRarity(uint256 tokenId) public pure returns (uint16) {
        return uint16(tokenId >> CARD_RARITY_BIT);
    }

    // identity for token id uri
    function getTokenIdentity(uint256 tokenId) public pure returns (uint256) {
        return tokenId >> CARD_IDENTITY_BIT;
    }

    function getTokenSkill(uint256 tokenId) public pure returns (uint32) {
        return uint32(tokenId >> CARD_PERSONALITY_SKILL_BIT);
    }

    function getTokenMintTime(uint256 tokenId) public pure returns (uint64) {
        return uint64(uint40(tokenId >> CARD_PERSONALITY_MINTTIME_BIT));
    }

    function getTokenIndex(uint256 tokenId) public pure returns (uint64) {
        return uint64(tokenId);
    }

    function getTokenPersonality(uint256 tokenId) public pure returns (uint256) {
        return uint256(uint136(tokenId));
    }

    function getSkillLength() public view returns (uint256) {
        return skillTypes.length();
    }

    function containSkill(uint32 _skill) public view returns (bool) {
        return skillTypes.contains(_skill);
    }

    function getIdentityFromCardId(uint256 _cardId) public view returns (uint256) {
        CardType storage card = cardTypes[_cardId];
        return
            ((uint256(NFT_SIGN) << NFT_SIGN_BIT) | (uint256(card.skin) << CARD_SKIN_BIT) | (uint256(card.rarity) << CARD_RARITY_BIT)) >>
            CARD_IDENTITY_BIT;
    }
}
","keccak256":"0x7716f4c4712922517bbb2ee1a354cb5308819d30802cb0cb43153f19f1978346","license":"MIT"},"contracts/CartoonToken.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity >=0.7.4;

import '@openzeppelin/contracts/token/ERC20/ERC20Capped.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';

contract CartoonToken is ERC20Capped, AccessControl {
    bytes32 public constant MINT_ROLE = keccak256('MINT_ROLE');
    bytes32 public constant ADMIN_ROLE = keccak256('ADMIN_ROLE');

    constructor(address _dev) ERC20('CartoonToken', 'CTO') ERC20Capped(130000000 * 10**18) {
        _setupRole(ADMIN_ROLE, _dev);
        _setRoleAdmin(MINT_ROLE, ADMIN_ROLE);
    }

    function mint(address to, uint256 amount) public {
        require(hasRole(MINT_ROLE, _msgSender()), 'Must have mint role');
        _mint(to, amount);
    }
}
","keccak256":"0x1b998b97ad6499cf4c51f2d49b95dd438d5f1d8b04dc88fbd59bd1c116adae6d","license":"MIT"},"contracts/ERC721Ex.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity >=0.7.4;
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721Pausable.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import '@openzeppelin/contracts/utils/EnumerableSet.sol';

import './CardSpec.sol';

contract ERC721Ex is ERC721Pausable, AccessControl {
    using SafeMath for uint256;
    using Strings for uint256;
    using EnumerableSet for EnumerableSet.UintSet;

    bytes32 public constant UPDATE_TOKEN_URI_ROLE = keccak256('UPDATE_TOKEN_URI_ROLE');
    bytes32 public constant MINT_ROLE = keccak256('MINT_ROLE');
    bytes32 public constant PAUSED_ROLE = keccak256('PAUSED_ROLE');
    bytes32 public constant ADMIN_ROLE = keccak256('ADMIN_ROLE');

    uint256 public tokenIndex;
    CardSpec public spec;

    // from -> identity -> tokens
    mapping(address => mapping(uint256 => EnumerableSet.UintSet)) IdentityTokens;

    constructor(CardSpec _spec, address _dev) ERC721('non-fungible Cartoon Token', 'NCTO') {
        spec = _spec;
        _setupRole(ADMIN_ROLE, _dev);
        _setRoleAdmin(UPDATE_TOKEN_URI_ROLE, ADMIN_ROLE);
        _setRoleAdmin(MINT_ROLE, ADMIN_ROLE);
        _setRoleAdmin(PAUSED_ROLE, ADMIN_ROLE);
    }

    // safe mint to someone
    function safeMint(
        address to,
        uint256 tokenPreId,
        bytes memory _data
    ) public returns (uint256 tokenId) {
        require(hasRole(MINT_ROLE, _msgSender()), 'Must have mint role');
        tokenId = tokenPreId | (uint256(uint40(block.timestamp)) << 64) | uint64(++tokenIndex);
        _safeMint(to, tokenId, _data);
    }

    // mint to someone
    function mint(address to, uint256 tokenPreId) public returns (uint256 tokenId) {
        require(hasRole(MINT_ROLE, _msgSender()), 'Must have mint role');
        tokenId = tokenPreId | (uint256(uint40(block.timestamp)) << 64) | uint64(++tokenIndex);
        _mint(to, tokenId);
    }

    function mintCard(address to, uint256 cardId) public returns (uint256 tokenId) {
        require(hasRole(MINT_ROLE, _msgSender()), 'Must have mint role');
        (uint16 skin, uint16 rarity, ) = spec.cardTypes(cardId);
        tokenId =
            (uint256(spec.NFT_SIGN()) << spec.NFT_SIGN_BIT()) |
            (uint256(skin) << spec.CARD_SKIN_BIT()) |
            (uint256(rarity) << spec.CARD_RARITY_BIT()) |
            (uint256(uint40(block.timestamp)) << 64) |
            uint64(++tokenIndex);
        _mint(to, tokenId);
        _setTokenURI(tokenId, string(abi.encodePacked(uint256(tokenId >> spec.CARD_IDENTITY_PADDING_BIT()).toString(), '.json')));
    }

    // burn token
    function burn(uint256 tokenId) public {
        //solhint-disable-next-line max-line-length
        require(_isApprovedOrOwner(_msgSender(), tokenId), 'caller is not owner nor approved');
        _burn(tokenId);
    }

    function setBaseURI(string memory baseURI) public {
        require(hasRole(UPDATE_TOKEN_URI_ROLE, _msgSender()), 'Must have update token uri role');
        _setBaseURI(baseURI);
    }

    function setTokenURI(uint256 tokenId, string memory tokenURI) public {
        require(hasRole(UPDATE_TOKEN_URI_ROLE, _msgSender()), 'Must have update token uri role');
        _setTokenURI(tokenId, tokenURI);
    }

    function pause() public whenNotPaused {
        require(hasRole(PAUSED_ROLE, _msgSender()), 'Must have pause role');
        _pause();
    }

    function unpause() public whenPaused {
        require(hasRole(PAUSED_ROLE, _msgSender()), 'Must have pause role');
        _unpause();
    }

    function approveBulk(address to, uint256[] memory tokenIds) public {
        for (uint256 i; i < tokenIds.length; ++i) {
            approve(to, tokenIds[i]);
        }
    }

    function getApprovedBulk(uint256[] memory tokenIds) public view returns (address[] memory) {
        address[] memory tokenApprovals = new address[](tokenIds.length);
        for (uint256 i; i < tokenIds.length; ++i) {
            tokenApprovals[i] = getApproved(tokenIds[i]);
        }
        return tokenApprovals;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override {
        super._beforeTokenTransfer(from, to, tokenId);
        uint256 identity = spec.getTokenIdentity(tokenId);
        if (from != address(0)) {
            IdentityTokens[from][identity].remove(tokenId);
        }
        if (to != address(0)) {
            IdentityTokens[to][identity].add(tokenId);
        }
    }

    function getIdentityTokenAt(
        address from,
        uint256 identity,
        uint256 index
    ) public view returns (uint256) {
        return IdentityTokens[from][identity].at(index);
    }

    function getIdentityTokenLength(address from, uint256 identity) public view returns (uint256) {
        return IdentityTokens[from][identity].length();
    }
}
","keccak256":"0xe8609ae2028047d4b40e0c2794b9fb9933b6112170ac62a3eb93f2f77511708a","license":"MIT"},"contracts/libraries/TransferHelper.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity >=0.7.4;

// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: APPROVE_FAILED');
    }

    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FAILED');
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FROM_FAILED');
    }

    function safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(success, 'TransferHelper: ETH_TRANSFER_FAILED');
    }
}
","keccak256":"0x2418324ec4322d1dc009a5e9295aa500a174a66011ea78c39ba93403c603da29","license":"MIT"},"contracts/pool/MasterChefNFT.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity >=0.7.4;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import '@openzeppelin/contracts/utils/EnumerableSet.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '../CartoonToken.sol';
import '../ERC721Ex.sol';
import '../libraries/TransferHelper.sol';

// Have fun reading it. Hopefully it's bug-free. God bless.
contract MasterChefNFT is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;
    // Info of each user.
    struct UserInfo {
        EnumerableSet.UintSet tokenIds;
        uint256 rewardDebt; // Reward debt. See explanation below.
    }
    // Info of each pool.
    struct PoolInfo {
        uint256 allocPoint; // How many allocation points assigned to this pool. CTOs to distribute per block.
        uint256 lastRewardBlock; // Last block number that CTOs distribution occurs.
        uint256 accCtoPerShare; // Accumulated CTOs per share, times 1e12. See below.
        uint256 tokenIdentity;
        uint256 tokenAmount;
    }
    // The CTO TOKEN!
    CartoonToken public cto;
    // card spec
    CardSpec public cardSpec;
    // erc721 token
    ERC721Ex public nCto;
    // CTO tokens created per block.
    uint256 public ctoPerBlock;
    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) private userInfo;
    // Total allocation poitns. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when CTO mining starts.
    uint256 public startBlock;
    event Deposit(address indexed user, uint256 indexed pid, uint256 tokenId);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 tokenId);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 tokenId);

    constructor(
        CartoonToken _cto,
        ERC721Ex _nCto,
        uint256 _ctoPerBlock,
        uint256 _startBlock,
        CardSpec _cardSpec
    ) {
        cto = _cto;
        nCto = _nCto;
        ctoPerBlock = _ctoPerBlock;
        startBlock = _startBlock;
        cardSpec = _cardSpec;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function getUserInfo(uint256 _pid, address _from) external view returns (uint256 tokenAmount, uint256 rewardDebt) {
        UserInfo storage user = userInfo[_pid][_from];
        return (user.tokenIds.length(), user.rewardDebt);
    }

    function containsUserTokenId(
        uint256 _pid,
        address _from,
        uint256 _tokenId
    ) external view returns (bool) {
        return userInfo[_pid][_from].tokenIds.contains(_tokenId);
    }

    function getUserTokenIndex(
        uint256 _pid,
        address _from,
        uint256 _index
    ) external view returns (uint256) {
        return userInfo[_pid][_from].tokenIds.at(_index);
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add(
        uint256 _allocPoint,
        uint256 _cardId,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        uint256 identity = cardSpec.getIdentityFromCardId(_cardId);
        poolInfo.push(
            PoolInfo({tokenIdentity: identity, allocPoint: _allocPoint, lastRewardBlock: lastRewardBlock, accCtoPerShare: 0, tokenAmount: 0})
        );
    }

    // Update the given pool's CTO allocation point. Can only be called by the owner.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public pure returns (uint256) {
        return _to.sub(_from);
    }

    // View function to see pending CTOs on frontend.
    function pendingCto(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accCtoPerShare = pool.accCtoPerShare;
        if (block.number > pool.lastRewardBlock && pool.tokenAmount != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 ctoReward = multiplier.mul(ctoPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accCtoPerShare = accCtoPerShare.add(ctoReward.mul(1e12).div(pool.tokenAmount));
        }
        return user.tokenIds.length().mul(accCtoPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward vairables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        if (pool.tokenAmount == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 ctoReward = multiplier.mul(ctoPerBlock).mul(pool.allocPoint).div(totalAllocPoint);

        // set allocPoint to zero if mint to cap
        cto.mint(address(this), ctoReward);
        pool.accCtoPerShare = pool.accCtoPerShare.add(ctoReward.mul(1e12).div(pool.tokenAmount));
        pool.lastRewardBlock = block.number;
    }

    // Deposit LP tokens to MasterChef for CTO allocation.
    function deposit(uint256 _pid, uint256 _tokenId) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);

        if (user.tokenIds.length() > 0) {
            uint256 pending = user.tokenIds.length().mul(pool.accCtoPerShare).div(1e12).sub(user.rewardDebt);
            safeCtoTransfer(msg.sender, pending);
        }
        if (_tokenId > 0) {
            require(cardSpec.getTokenIdentity(_tokenId) == pool.tokenIdentity, 'token identity mismatch');
            TransferHelper.safeTransferFrom(address(nCto), msg.sender, address(this), _tokenId);
            user.tokenIds.add(_tokenId);

            pool.tokenAmount = pool.tokenAmount.add(1);
        }
        user.rewardDebt = user.tokenIds.length().mul(pool.accCtoPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _tokenId);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 _pid, uint256 _tokenId) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        uint256 pending = user.tokenIds.length().mul(pool.accCtoPerShare).div(1e12).sub(user.rewardDebt);
        safeCtoTransfer(msg.sender, pending);
        if (_tokenId > 0) {
            require(user.tokenIds.contains(_tokenId), 'withdraw: not good');
            user.tokenIds.remove(_tokenId);
            TransferHelper.safeTransferFrom(address(nCto), address(this), msg.sender, _tokenId);
            pool.tokenAmount = pool.tokenAmount.sub(1);
        }
        user.rewardDebt = user.tokenIds.length().mul(pool.accCtoPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _tokenId);
    }

    function emergencyWithdraw(uint256 _pid, uint256 _tokenId) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.tokenIds.contains(_tokenId), 'emergencyWithdraw: not good');
        user.tokenIds.remove(_tokenId);
        TransferHelper.safeTransferFrom(address(nCto), address(this), msg.sender, _tokenId);
        pool.tokenAmount = pool.tokenAmount.sub(1);
        user.rewardDebt = user.tokenIds.length().mul(pool.accCtoPerShare).div(1e12);
        emit EmergencyWithdraw(msg.sender, _pid, _tokenId);
    }

    // Safe cto transfer function, just in case if rounding error causes pool to not have enough CTOs.
    function safeCtoTransfer(address _to, uint256 _amount) internal {
        uint256 ctoBal = cto.balanceOf(address(this));
        if (_amount > ctoBal) {
            cto.transfer(_to, ctoBal);
        } else {
            cto.transfer(_to, _amount);
        }
    }
}
