        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut,
        address token0,
        address token1
    ) public view returns (uint256 amountIn) {
        return IMdexFactory(factory).getAmountIn(amountOut, reserveIn, reserveOut, token0, token1);
    }

